package feb27.com;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x=10;
    x++;
    System.out.println(x);
    int y=20;
    y+=20;
    System.out.println(y);
    int a=30;
    a--;
    System.out.println(a);
    int b=40;
    b-=40;
    System.out.println(b);
	}

}
